namespace AlgorithmPractice.LeetCode.Easy.Sorting
{
    public static class FindTheDifference_389
    {
        public static void Run()
        {
            string s = "";
            string t = "";
            var result = Solution(s, t);
        }

        private static char Solution(string s, string t)
        {
            var dictionary = new Dictionary<char, int>();

            for (int i = 0; i < s.Length; i++)
            {
                if (!dictionary.ContainsKey(s[i]))
                {
                    dictionary.Add(s[i], 1);
                }
                else
                {
                    dictionary[s[i]]++;
                }
            }

            for (int i = 0; i < t.Length; i++)
            {
                if (!dictionary.ContainsKey(t[i]))
                {
                    return t[i];
                }
                else
                {
                    dictionary[t[i]]++;
                }
            }

            foreach (var item in dictionary)
            {
                if (item.Value % 2 != 0)
                {
                    return item.Key;
                }
            }

            return 't';
        }
    }
}
